import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "../components/Navbar";

const RoomDetails = () => {
  const { id } = useParams();

  const room = {
    title: "Deluxe Room",
    hotel: "Sea Breeze Resort",
    rating: 4.8,
    price: 100,
    capacity: 2,
    description: "Spacious and beautifully designed room with a stunning view. Includes Wi-Fi, breakfast, and access to all resort amenities.",
    image: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c?auto=format&fit=crop&w=1600&q=80"
  };

  return (
    <div className="dark:bg-gray-900 bg-gray-100 min-h-screen">
      <Navbar />
      <div className="max-w-5xl mx-auto p-6">
        <div className="rounded-lg overflow-hidden shadow-lg bg-white dark:bg-gray-800">
          <img src={room.image} alt={room.title} className="w-full h-80 object-cover" />
          <div className="p-6 space-y-4">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">{room.title}</h1>
            <p className="text-sm text-gray-500 dark:text-gray-300">üìç {room.hotel} ‚Äî ‚≠ê {room.rating}</p>
            <p className="text-lg text-gray-700 dark:text-gray-200">{room.description}</p>
            <p className="text-xl font-bold text-blue-600">${room.price} <span className="text-sm font-normal text-gray-600">/ night</span></p>
            <button className="text-white bg-green-600 hover:bg-green-700 px-6 py-3 rounded-lg text-base font-medium">Book Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RoomDetails;
