import React, { useState, useEffect } from "react";

const Navbar = () => {
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");

  useEffect(() => {
    document.documentElement.className = theme;
    localStorage.setItem("theme", theme);
  }, [theme]);

  return (
    <div className="bg-white dark:bg-gray-900 shadow-md p-4 flex justify-between items-center sticky top-0 z-50">
      <h1 className="text-2xl font-bold text-blue-600">TopStay</h1>
      <div className="flex gap-4 items-center">
        <button className="text-sm font-medium text-gray-700 dark:text-gray-300 hover:underline">Login</button>
        <button className="text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 px-4 py-1 rounded">Sign up</button>
        <button
          onClick={() => setTheme(theme === "light" ? "dark" : "light")}
          className="ml-3 px-2 py-1 bg-gray-200 dark:bg-gray-700 rounded"
        >
          {theme === "light" ? "üåô" : "‚òÄÔ∏è"}
        </button>
      </div>
    </div>
  );
};

export default Navbar;
