import React from "react";
import { Link } from "react-router-dom";

const rooms = [
  {
    id: "deluxe",
    title: "Deluxe Room",
    hotel: "Sea Breeze Resort",
    price: 100,
    guests: 2,
    rating: 4.8,
    image: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c?auto=format&fit=crop&w=1600&q=80"
  },
  {
    id: "suite",
    title: "Suite Room",
    hotel: "Grand Lux Hotel",
    price: 150,
    guests: 3,
    rating: 4.9,
    image: "https://images.unsplash.com/photo-1552903023-9ec09fecd5bb?auto=format&fit=crop&w=1600&q=80"
  },
  {
    id: "standard",
    title: "Standard Room",
    hotel: "Budget Inn",
    price: 80,
    guests: 2,
    rating: 4.2,
    image: "https://images.unsplash.com/photo-1505692794403-55b49f79b256?auto=format&fit=crop&w=1600&q=80"
  },
  {
    id: "luxury",
    title: "Luxury Room",
    hotel: "Elite Stay",
    price: 200,
    guests: 4,
    rating: 5.0,
    image: "https://images.unsplash.com/photo-1600585153821-1ec173cfe125?auto=format&fit=crop&w=1600&q=80"
  }
];

const HomePage = () => {
  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-10">Available Rooms</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
        {rooms.map((room) => (
          <div key={room.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-md overflow-hidden">
            <img src={room.image} alt={room.title} className="w-full h-52 object-cover" />
            <div className="p-5 space-y-2">
              <div className="flex justify-between items-center">
                <h2 className="text-xl font-semibold">{room.title}</h2>
                <span className="text-yellow-500 font-medium">‚≠ê {room.rating}</span>
              </div>
              <p className="text-sm text-gray-500 dark:text-gray-300">üìç {room.hotel} ‚Äî üõèÔ∏è {room.guests} guests</p>
              <p className="text-lg font-bold">${room.price} <span className="text-sm font-normal">/ night</span></p>
              <Link
                to={`/rooms/${room.id}`}
                className="block text-center bg-blue-600 hover:bg-blue-700 text-white py-2 rounded mt-2"
              >
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
