import React from "react";
import { Link } from "react-router-dom";

const RoomCard = ({ room }) => (
  <div className="border rounded p-4 shadow dark:bg-gray-800 dark:text-white">
    <img src={room.image} alt={room.title} className="h-40 w-full object-cover mb-2 rounded" />
    <h2 className="text-xl font-semibold">{room.title}</h2>
    <p className="text-gray-600 dark:text-gray-300">${room.price} / night</p>
    <Link to={`/room/${room.id}`} className="text-blue-500 underline mt-2 inline-block">
      View Details
    </Link>
  </div>
);

export default RoomCard;
