import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "../components/Navbar";

const RoomDetails = () => {
  const { id } = useParams();

  return (
    <>
      <Navbar />
      <div className="p-6 dark:text-white">
        <h1 className="text-2xl font-bold">Room Details</h1>
        <img
          src="https://source.unsplash.com/featured/?hotel"
          alt="Room"
          className="w-full h-64 object-cover rounded my-4"
        />
        <p>This is the detail page for room ID: {id}. You can include more info here.</p>
      </div>
    </>
  );
};

export default RoomDetails;
